        background: #00000010;
        border-radius: 10px;
        /*         width:2px; */
        transition: all .2s ease;
    }
    .discus {
        border: solid 0px #e2e2e2;
        border-radius: 10px;
        box-shadow: 0 0 10px 2px #00000023;
        background: #fcfcfc;
        margin-bottom: 30px;
        margin-top: -20px;
    }
    #showTopics {
        /*         margin-top:20px!important; */
        width: 95%;
        margin: 20px auto 0;
    }

    .left {
        box-shadow: 0 0 10px 2px #00000012;

        width: var(--left-width)!important;
        border-radius: 10px;
        z-index: 100;
    }
    .left .content,
    .left .content1 {
        /*     position: relative; */
        border: 0px solid #ddd;



        padding-bottom: 30px;
        padding-top: 10px;
        height: calc(var(--content-height) - 100px);
        overflow: scroll;
    }
    .newRight {
        width: calc(100% - 25px)!important;
        /*     box-shadow: 0 0 0px 10px #ffffff; */
        /* z-index:10; */
        padding: 0 13px;
        /*         position: sticky; */
        top: -30px;
        /*         background:#fff; */
    }
    .newTopic {
        width: calc(100% - 20px)!important;
        height: 28px;
        border: solid 1px #ddd;

        /*     box-shadow: 0 0 4px 1px #00000023; */
        background: #fff;
        border-radius: 5px;
        /*     font-family: Microsoft YaHei; */
        /*     color: #999; */
        padding: 0 10px;
        line-height: 28px;
    }
    .newPic {
        margin-top: 5px;
        position: relative;
        width: 100%!important;
        margin-bottom: 15px;
    }
    .newText {
        width: 428px;
        border: solid 1px #ddd;
        resize: none;
        background: #fff;
        border-radius: 2px;
        height: 100px;
        padding: 10px;
        font-family: Microsoft YaHei;
        line-height: 2;
    }
    .continue-study {
        /*     height: 30px; */
        background: #ffffff00;
        border: 0px solid #ddd;
        border-bottom: 1px solid #ddd!important;
        line-height: 37px;
        font-size: 18px;
        padding: 10px 10px 10px 20px;
    }
    .left .content,
    .left .content1::-webkit-scrollbar {
        width: 5px;
    }
    .left .content,
    .left .content1::-webkit-scrollbar-thumb {
        background: #00000010;
        border-radius: 10px;
        /*         width:2px; */
        transition: all .2s ease;
    }
    .left .content,
    .left .content1::-webkit-scrollbar-thumb:hover {
        background: #00000033;
        border-radius: 10px;
        width: 2px;
        transition: all .2s ease;
    }
}
