/* ==UserStyle==
@name           首页全屏
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document url("https://www.bilibili.com"),
url("https://www.bilibili.com/") {
    :root {
        --banner-height: calc(100vh - 108px);
    }
    #internationalHeader {
        height: 100vh;
    }
    .bili-banner {}
    #van-popover-7548 {
        /*     display: block!important; */
        /*         height:100px; */
    }
}

