sections.code:
:root {
        /*         --player-width: 1175px; */
        --title-height: calc(110px);
        --profile-height: 100px;
        /*     --screen-height:690px; */
        --screen-height: 87vh;
        --top-distance: 136px;
        --topbar-height: 56px;

        --sidenav-width: 240px;
        --fav-main-width: calc(100vw - var(--sidenav-width));
        --fav-bg: #00000004;
        --fav-bg-solid: #f9f9f9;

        --r-con-width: 410px;
        --wrap-width: 96vw;
        --player-width: calc(var(--wrap-width) - var(--r-con-width) - 30px);
        --player-height: calc(var(--player-width) / 16 * 9 - 0px);
        --content-position: calc(var(--topbar-height) + 20px + var(--player-height) + var(--danmaku-sendbar-height) + 20px);
        --upinfo-right-distance: calc(var(--r-con-width) + (100vw - var(--wrap-width)) / 2 + 30px + 2px);
        --upinfo-left-distance: calc((100vw - var(--wrap-width)) / 2);
        --up-info-height: 120px;
        --danmaku-sendbar-height: 0px;
        /*         --wrap-padding:76px; */
        --pic-width: 170px;
        --pic-height: calc(170px / 16 * 9);
    }

#app {
        display: flex;
        justify-content: center;
    }
    .v-wrap {
        width: var(--wrap-width)!important;
    }
    .v-wrap {
        /*     width: 988px; */
        padding: 0!important;
        max-width: 100vw!important;
        min-width: 0vw!important;
    }
    .l-con {
        width: 100%!important;
    }
    #bofqi {
        width: 100%!important;
        height: var(--player-height)!important;
    }
    .bilibili-player-video-sendbar {
        position: fixed;
        top: calc(var(--topbar-height) + 20px);
        /*         height: calc(40px); */
        width: calc(var(--player-width) - 20px);
        padding: 0 10px;
        /*         display:flex; */
        /*         background:#fff; */
        opacity: 0;
        /*         backdrop-filter: blur(15px); */
        transition: all .5s ease;
        z-index: 10000;
    }
    .bilibili-player-video-sendbar:hover {
        /*             position:fixed; */
        /*             top:0; */
        /*         height: 46px; */
        opacity: 1;
        z-index: 19231;
        transition: all .5s ease;
        filter: blur(0px);
    }
    .bilibili-player-video-danmaku-setting-wrap {
        top: 46px;
    }
    .bilibili-player-mode-selection-container {
        top: 39px;
        height: fit-content;
    }
    .bilibili-player-video-sendbar .bilibili-player-video-danmaku-root .bilibili-player-video-danmaku-switch .choose_danmaku {
        top: 38px;
    }
    #viewbox_report {
        position: absolute;
        top: calc(var(--content-position));
        max-width: calc(var(--player-width) - 320px - 10px);
    }
    /*     .v-wrap .s_tag {
        position: relative;
        clear: both;
        margin-bottom: 20px;
        padding-bottom: var(--up-info-height);
        border-bottom: 1px solid #e5e9ef;
    } */
    .bilibili-player .bilibili-player-area.video-state-blackside .bilibili-player-video {
        margin: 0;
    }
    #arc_toolbar_report {
        margin-top: calc(var(--title-height) + 10px);
    }
    #v_upinfo {
        /*     background-color:#fff; */
        /*     width: 100%; */
        padding: 10px 0 10px 0;
        /*     box-shadow:0 0 5px 10px #fff; */
        position: absolute;
        top: calc(var(--content-position));
        right: var(--upinfo-right-distance);
        /*         left: var(--upinfo-left-distance); */
    }

    .list-box {
        max-height: calc(var(--screen-height) - var(--top-distance))!important;
        /*     max-height:100000000px!important; */
    }
    /* #reco_list{
    margin-top:calc(var(--profile-height) + 10px);
} */
    #slide_ad {
        display: none;
    }
    .nav-link-item span a {
        display: none;
    }
    .r-con {
        width: var(--r-con-width);
    }
    #right-bottom-banner,
    #live_recommand_report {
        display: none;
    }
    .video-page-card {
        padding: 4px 0;
    }
    .video-page-card .card-box .pic-box .pic {
        border-radius: 0px;
    }
    #internationalHeader {
        position: fixed;
        top: 0;
        width: 100vw;
        z-index: 10101;
    }
    #app {
        padding-top: 56px;
        background: var(--fav-bg);
    }
    .v-wrap .r-con {
        width: var(--r-con-width);
    }
    .bb-comment,
    .comment-bilibili-fold {
        background: none;
    }
    .player-auxiliary-area {
        width: 100%;
    }
    .player-auxiliary-area .player-auxiliary-danmaku .player-auxiliary-danmaku-function [class*=player-auxiliary-danmaku-btn-].player-auxiliary-danmaku-btn-danmaku,
    .danmaku-wrap .player-auxiliary-area .player-auxiliary-danmaku-wrap .player-auxiliary-danmaku-list .danmaku-info-row span.danmaku-info-danmaku {
        width: 60%;
    }
    .video-page-card .card-box .pic-box,
    .video-page-operator-card .pic-box[data-v-0b7c807d], .pic-box,.video-page-operator-card .pic-box .pic[data-v-0b7c807d],.video-page-card .card-box .pic-box .pic, .video-page-card .card-box .pic-box .pic img,.video-page-operator-card .pic-box .pic img[data-v-0b7c807d]{
        width: var(--pic-width);
        height: var(--pic-height);
        border-radius: 0px;

    }
    @media screen and (max-width: 1200px) {
        :root {
            --r-con-width: 326px;
        }
    }
    @media screen and (max-width: 1000px) {
        :root {
            --wrap-width: calc(100vw - 2 * var(--wrap-padding));
            --wrap-padding: 20px;
            --player-width: calc(var(--wrap-width) - var(--r-con-width) - 30px);
            --player-height: calc(var(--player-width) / 16 * 9);
        }

        .v-wrap {
            width: var(--wrap-width)!important;
            padding: 0 0!important;
        }
        .bilibili-player-video-sendbar {
            top: calc(var(--topbar-height));
        }
        #v_upinfo {
            /*     background-color:#fff; */
            /*     width: 100%; */
            padding: 0;
            width: var(--player-width);
            /*     box-shadow:0 0 5px 10px #fff; */
            position: absolute;
            top: calc(var(--content-position) + var(--title-height) + 40px);
            left: var(--wrap-padding);
            z-index: 10;
        }
        #arc_toolbar_report {
            margin-top: calc(var(--title-height));
            margin-bottom: calc(var(--up-info-height));
        }
        .l-con {
            width: var(--player-width)!important;
            margin: 0;
            padding: 0;
        }
        #viewbox_report {
            max-width: calc(var(--player-width));
        }
    }
    @media screen and (max-width: 900px) {
        :root {
            --r-con-width: 30vw;
        }
    }
    @media screen and (max-width: 800px) {
        :root {
            --wrap-padding: 0px;
            --r-con-width: 0;
            --l-con-width: 100%;
            --player-width: 100vw;
            --content-padding: 20px;
            --video-list-fold-height: 50px;
            --video-list-expand-height: calc(100vh - var(--video-list-position) - 20px);
            --video-list-position: calc(var(--topbar-height) + 20px + var(--player-height) + var(--danmaku-sendbar-height));
            --content-position: calc(var(--topbar-height) + 20px + var(--player-height) + var(--danmaku-sendbar-height) + var(--video-list-fold-height));
        }
        .v-wrap {
            margin: 0;
        }
        #viewbox_report,
        #arc_toolbar_report,
        #v_upinfo,
        #v_desc,
        #v_tag,
        #comment {
            left: var(--content-padding);
            width: calc(100vw - var(--content-padding) * 2);
        }
        .float-nav {
            display: none;
        }
        .l-con {
            width: 100%!important;
            margin: 0;
            padding: 0;
        }
        .r-con {
            margin-left: 0!important;
            overflow: hidden;
        }
        #multi_page {
            position: absolute;
            top: var(--video-list-position);
            left: var(--content-padding);
            height: 40px;
            overflow: hidden;
            width: calc(100vw - var(--content-padding) * 2);
            transition: all .2s ease;
        }
        .head-con {
            padding-bottom: 6px!important;
        }
        #multi_page:hover {
            /*             background:#000; */
            z-index: 100000;
            /*             height:fit-content; */
            height: var(--video-list-expand-height);
            transition: all .2s ease;
            box-shadow: 0 10px 20px 5px #00000032;
        }
        #arc_toolbar_report {
            margin-top: calc(var(--title-height) + var(--video-list-fold-height));
            display: flex;
            height: 50px;
            /*             overflow:hidden; */
        }
        .video-toolbar .ops {
            display: flex;
            flwx-wrap: nowrap;
            justify-content: space-evenly;
            width: 100%;
        }
        .video-toolbar .ops span {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        .video-toolbar .ops span i {
            margin: 0;
        }
        .video-toolbar .more {
            width: 30px;
        }
        .video-toolbar .appeal-text {
            width: 0;
            overflow: hidden;
            margin: 0!important;
        }
        #v_upinfo {
            top: calc(var(--content-position) + var(--title-height) + 60px);
        }
        .list-box {
            /*             height: fit-content; */
            height: calc(var(--video-list-expand-height) - 40px - 10px);
        }
    }

.container {
        max-width: 100vw!important;
        margin: 20px 0;
        padding: 0 20px!important;
    }
    #playContainer {
        width: 99vw!important;
    }

.wrapper {
        width: 100%!important;
    }
    /*隐藏滚轮*/
    /*     ::-webkit-scrollbar {
        display: none;
    } */
    .h-inner {
        padding-top: 12px!important;
    }
    .icon-ic_collect,
    .icon-ic_sub,
    .icon-ic_channel,
    .icon-ic_video,
    .icon-ic_following,
    .icon-ic_home,
    .icon-ic_setting {
