        z-index: 123;
    }

.h-inner {
        height: 0;
        margin: 0;
        padding: 0!important;
        overflow: hidden;
        /*             transition: all ease-in-out .2s; */
    }
    #navigator {
        position: relative;
        top: 0px;
    }
