        position: relative;
        width: auto;
    }
    #app {
        margin-top: 0px;
    }



    #page-fav .fav-sidenav .fav-list-container {
        /*     max-height: calc(var(--screen-height) - 20px); */
        max-height: 100000000000px;
    }
    .favList-info {
        display: none!important;
        margin: 0;
        padding: 0;
    }
    .n {
        margin-bottom: 0px;
    }
    #page-fav .fav-main {
        /*     width: 1300px; */
        width: var(--fav-main-width);
        background: var(--fav-bg);
    }
    .fav-video-list {
        display: flex;
        /*     align-items:center; */
        justify-content: space-evenly;
        flex-wrap: wrap;
        /*     height: calc(var(--screen-height) - 80px); */
        overflow: scroll;
    }
    .video-edit {
        margin-right: 12px;
    }
    #page-fav .fav-main .small-item:nth-child(5n) {
        margin-right: 5px;
    }
    #page-fav .fav-main .small-item {
        padding: 0;
        margin: 0 5px 20px 0;
        border-radius: 0px;
        border: 0px solid #fff;
        box-sizing: border-box;
    }
    .small-item .cover {
        background: url(//s1.hdslb.com/bfs/static/jinkela/space/asserts/video-placeholder.png) 50%;
        background-size: cover;
        border-radius: 0px;
        display: block;
        width: 160px;
