        overflow: hidden;
        position: relative;
    }
    .small-item img {
        border-radius: 0px;
        display: block;
        width: 160px;
        height: 100px;
    }
    .be-pager {
        background-color: var(--fav-bg-solid);

        position: fixed;
        bottom: -50px;
        /*     bottom: -100px; */
        z-index: 5;
        /*     background: #fff; */
        width: var(--fav-main-width);
        /*     height: 100px; */
        padding: 10px 0 10px 0;
        /*     box-shadow:0 0px 20px -10px #00000029; */
    }

    .be-pager-item {
        line-height: 25px;
        padding: 0 8px;
